mapper.select:
select blog_article.id, blog_article.section_id, blog_article.poster, blog_article.tag_ids, blog_article.tag_names, blog_article.article_title, blog_article.article_digest, blog_article.visit_type, blog_article.create_time, blog_article.update_time from blog_article join blog_section on blog_section.id = blog_article.section_id
blog_article.del_flag = 0
and blog_article.tenant_id = #{dto.tenantId}
and blog_article.section_id = #{dto.sectionId}
and find_in_set(#{dto.tagId},blog_article.tagId)
and blog_article.articleTitle like CONCAT("%",#{dto.articleTitle})
and blog_article.publish_flag = #{dto.publishFlag}
LIMIT #{page.offset},#{page.size} ;

select
count(1)
from blog_article
join blog_section on blog_section.id = blog_article.section_id

    blog_article.del_flag = 0

        and blog_article.tenant_id = #{dto.tenantId}


        and blog_article.section_id = #{dto.sectionId}


        and find_in_set(#{dto.tagId},blog_article.tagId)


        and blog_article.articleTitle like CONCAT("%",#{dto.articleTitle})


        and blog_article.publish_flag = #{dto.publishFlag}
